import React from 'react'
import {Link} from 'react-router-dom'
import logo from '../logo.svg';

export default class Header extends React.Component{
  constructor(props){
    super(props)
    this.state={loading:this.props.loading}
  }
  render(){
    console.log(this.props.state.loading)
    return (
      <header>
        <Link to="/"><img width="80" src={logo} className={(this.state.loading)?"loading-logo":""} alt="logo" /></Link>
      </header>
    )
  }
}
