import React from "react";
import Header from "./Header";
import axios from 'axios'
export default class BeerList extends React.Component{
  constructor(){
    super()
    this.state={beer:null}
  }
  componentWillMount(){
    console.log(this.props)
    axios.get('https://ironbeer-api.herokuapp.com/single/'+this.props.match.params.id)
    .then(res=>{
      this.setState({beer:res.data})
    })
    .catch(e=>console.log(e))
  }
  render(){
    console.log(this.state)
    const { image_url, name, tagline, contributed_by }=this.state.beer 
    return (
      <div>
        <Header></Header>
        <img width="50px" src={image_url} alt={name} />
        <ul>
          <li>
            <h2>{name}</h2>
          </li>
          <li>{tagline}</li>
          <li>contributed by: {contributed_by}</li>
        </ul>
      </div>
    );
  }
};
