import React from 'react'
import Header from './Header';
import axios from 'axios'
export default class NewBeer extends React.Component{
  constructor(){
    super()
    this.state={
      name:"",
      tagline:"",
      description:"",
      first_brewed:"",
      brewers_tips:"",
      attenuation_level:"",
      contributed_by:""
    }
  }
  createBeer=()=>{
    // axios.post("https://ironbeer-api.herokuapp.com/beers/new",beer)
    // .then(res=>console.log("res",res))
    // .catch(e=>console.log("Error",e))
  }
  render(){
    return (
      <div>
        <Header></Header>
        <label >Name</label>
        <input onChange={e=>this.setState({name:e.target.name})} value={this.state.name} type="text"/>
        <label >Tagline</label>
        <input onChange={e=>this.setState({name:e.target.tagline})} value={this.state.tagline} type="text"/>
        <label >Description</label>
        <textarea onChange={e=>this.setState({name:e.target.description})} value={this.state.description} type="text"/>
        <label >First Brewed</label>
        <input onChange={e=>this.setState({name:e.target.first_brewed})} value={this.state.first_brewed} type="text"/>
        <label >Brewers tips</label>
        <input onChange={e=>this.setState({name:e.target.brewers_tips})} value={this.state.brewers_tips} type="text"/>
        <label >Attenuation Level</label>
        <input onChange={e=>this.setState({name:e.target.attenuation_level})} value={this.state.attenuation_level} type="text"/>
        <label >Contributed By</label>
        <input onChange={e=>this.setState({name:e.target.contributed_by})} value={this.state.contributed_by} type="text"/>

        <button onClick={this.createBeer}>Add New</button>
      </div>
    )
  }
}
