import React from "react";
import Header from "./Header";
import axios from 'axios'
export default class BeerList extends React.Component {
  constructor() {
    super()
    this.state = { beer: {} }
  }
  componentWillMount() {
    console.log(this.props)
    axios.get('https://ironbeer-api.herokuapp.com/beers/single/' + this.props.match.params.id)
      .then(res => {
        this.setState({ beer: res.data })
      })
      .catch(e => console.log(e))
  }
  render() {
    console.log(this.state)
    const { image_url, name, tagline, first_brewed, attenuation_level, description, contributed_by } = this.state.beer
    return (
      <div>
        <Header></Header>
        <ul>
          <li>
            <img width="50px" src={image_url} alt={name} />
          </li>
          <li>
            <h2>{name}</h2><span>{attenuation_level}</span>
          </li>
          <li>{tagline}<span>{first_brewed}</span></li>
          <li>{description}</li>
          <li>contributed by: {contributed_by}</li>
        </ul>
      </div>
    );
  }
};
